package template

import (
	"io"
	gotemplate "text/template"
	"time"
)

// the {{ tick }} stuff is because this https://github.com/golang/go/issues/18221#issuecomment-394255883

const airlineDataTemplate string = `package {{ .Package }}

// this file was generated by the go-sfomuseum-airlines-tools package on {{ .LastUpdate }}
{{ $tick := "` + "`" + `" }}
const AirlineData string = {{ $tick }}{{ .Data }}{{ $tick }}
`

type AirlineDataVars struct {
	Package    string
	Data       string
	LastUpdate string
}

func RenderAirlineData(wr io.Writer, vars *AirlineDataVars) error {

	now := time.Now()

	vars.LastUpdate = now.Format(time.RFC3339)

	t := gotemplate.New("data")
	t, err := t.Parse(airlineDataTemplate)

	if err != nil {
		return err
	}

	return t.Execute(wr, vars)
}
